import React from 'react';
import axios from 'axios';

import "./styles/realizarCompra.css"; 

export class RealizarCompra extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            articulos : [],
            total : 0,
            art : "",
            cantidad : 0,
            orden : []
        };
    }

    async componentDidMount() {
        const articulos = await axios.get(`http://localhost:5000/`);
        this.setState({articulos : articulos.data});
        console.log(this.state.articulos);
    };

    onChange = (e) =>{
        let precio = 0;

        for (let i = 0; i < this.state.articulos.length; i++){            
            if(this.state.articulos[i].id === this.state.art){
                precio = this.state.articulos[i].precio;
                break;
            };
        };
        this.setState({total : e.target.value * precio});
        this.setState({cantidad : e.target.value});
    };

    onChangeArticulo = (e) =>{
        this.setState({art : e.target.value });
    };

    onSubmit = (e) =>{
       
        this.state.orden.push([this.state.art, this.state.total, this.state.cantidad]);
        console.log(this.state.orden);
    };

    render() {
        return (
            <>
               <div className="container_realizar_compra">
                   <h1>Realizar <span>compra</span></h1>

                   <div className="form_wrapper">
                       <div className="form_info">
                            <h3>Orden</h3>
                            <form onSubmit={this.onSubmit}>
                                <p className="block">
                                    <label htmlFor="orden">Orden: </label>
                                    <input type="text" name="orden" id="orden" disabled={true}/>
                                </p>
                                <p>
                                    <label htmlFor="nombre">Nombre: </label>
                                    <input type="text" name="nombre" id="nombre"/>
                                </p>
                                <p>
                                    <label htmlFor="fecha">Fecha:</label>
                                    <input type="date" name="fecha" id="fecha"/>
                                </p>
                                <p>
                                    <label htmlFor="articulo">Articulo:</label>
                                    <select onChange={this.onChangeArticulo}>
                                        <option>--Seleccionar--</option>
                                        {this.state.articulos.map((articulo, index) =>(
                                            <option 
                                            key={index} 
                                            name={articulo.id} 
                                            id={articulo.id}                                            
                                            value={articulo.id}>
                                                {articulo.descripcion}                                                
                                            </option>
                                        ))}
                                    </select>
                                </p>
                                <p>
                                    <label htmlFor="cantidad">Cantidad:</label>
                                    <input
                                        type="number" 
                                        name="cantidad" 
                                        id="cantidad" 
                                        placeholder="0"
                                        min="0"
                                        onChange={this.onChange}/>
                                </p>
                                <p className="block">
                                    <label htmlFor="subtotal">Subtotal:</label>
                                    <input 
                                        type="text" 
                                        name="subtotal" 
                                        id="subtotal" 
                                        placeholder={this.state.total} 
                                        disabled={true}/>
                                </p>
                                <p className="block">
                                    <button type="submit">Agregar</button>
                                </p>
                            </form>
                       </div>
                       
                       <div className="detalle_orden">
                            <h3>Detalle de Orden</h3>

                            <table>
                                <thead>
                                    <tr>
                                        <th>Articulo</th>
                                        <th>Cantidad</th>
                                        <th>Subtotal</th>
                                        <th>Eliminar</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {this.state.orden.map((orden, index) =>(
                                    <tr>
                                        
                                            <td>{orden[0]}</td>
                                            <td>{orden[1]}</td>
                                       
                                    </tr>
                                     ))}
                                </tbody>
                            </table>
                       </div>
                   </div>
                </div>     
            </>
        );
    };
};
